import React from 'react'

export default function RatingForm() {
  return (
    <>

    <div>RatingForm</div>

    <form onSubmit={handleSubmit(onsubmit)}>
          <div className="form-group">
            <label htmlFor="email">Enter Email</label>
            <input
              type="email"
              className="form-control"
              id="email"
              placeholder=" Email"
              name="email"
              {...register("email", {
                onChange: (e) => {
                  setBody({ ...body, [e.target.name]: e.target.value });
                },
                required: { value: true, message: "Enter Email" },
              })}
            />
            {errors.email && (
              <small style={{ color: "red" }}> {errors.email.message} </small>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="password">Enter Password</label>
            <input
              type="password"
              className="form-control"
              id="password"
              placeholder=" Password"
              name="password"
              {...register("password", {
                onChange: (e) => {
                  setBody({ ...body, [e.target.name]: e.target.value });
                },
                required: { value: true, message: "Enter Password" },
                minLength: { value: 3, message: "Enter minimum 3 Character" },
                maxLength: { value: 8, message: "Enter maximum 8 Character" },
              })}
            />
            {errors.password && (
              <small style={{ color: "red" }}>
                {" "}
                {errors.password.message}{" "}
              </small>
            )}
          </div>

          <button onClick={()=> nevigate} type="submit" id="submitbtn" className="btn btn-success">
            Login
          </button>
        </form>









    <div className="dropdown">
  <button className="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
    Rating
  </button>
  <ul className="dropdown-menu">
    <li><a className="dropdown-item" href="\">1</a></li>
    <li><a className="dropdown-item" href="\">2</a></li>
    <li><a className="dropdown-item" href="\">3</a></li>
    <li><a className="dropdown-item" href="\">4</a></li>
    <li><a className="dropdown-item" href="\">5</a></li>

  </ul>
</div>

<div className="input-group mb-3">
    <h1>Feedback</h1>
  <input type="text" className="form-control" aria-label="Sizing example input" aria-describedby="inputGroup-sizing-default" placeholder='write your feedback'/>
</div>



    </>
  )
}
