import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function RatingTake() {

const id = useParams();
const _id = id.id;

const [data  ,setData]  = useState([]);

const getRatingById = () => {
  axios.get(`http://localhost:3030/rating/${_id}`).then((res)=>{
    const MyData = res.data
    setData(MyData)
  }).catch((err)=>{
    console.log(err)
  })
}

useEffect(()=>{
  getRatingById()
},[])

  return (
    <>
      <div>RatingTake</div>
      <div className="card-body">
        <h5 className="card-title">Hotel-Name : - {data.hName}</h5>
          <h5 className="card-title" >Address : - {data.address}</h5>
          <h5 className="card-title">Phone-Number : - {data.hotelNumber}</h5>
          <h5 className="card-title">Reason To Choose : - {data.reason}</h5>
          <button>
            {/* <Link to={`/Ratingt/${data._id}`}>View-More</Link> */}
            <Link to={`/Ratingt/${data._id}`}>Rating-Give</Link>

          </button>
        </div>
    </>
  );
}
