    const [data, setData] = useState([]);

    const getDataById = () =>{
      axios.get("http://localhost:3030/singleGet").then((res)=>{
        console.log(res);
        const myData = res.data
        setData(myData)
      }).catch((err)=>{
        console.log(err)
      })
    }
  
   useEffect(()=>{
    getDataById()
   },[])
   console.log(data)

  return (
    <>
        <div>ViewIt</div>
       

        {
     data.map((item) =>
     <div id="first" key={item._id} className="card">
        <img style={{height : "340px"}} src={item.hotelImg} className="card-img-top" alt="..."/>
        <div className="card-body">
          <h5 className="card-title" >address {item.address}</h5>
          <h5 className="card-title">phonenumber{item.hotelNumber}</h5>
          {/* <h5 className="card-title">hotel-name{item.hName}</h5>
          <h5 className="card-title">hotel-name{item._id}</h5> */}

          <button>
            <Link to={`/view/${item._id}`}>View-More</Link>
          </button>
        </div>
      </div>
)
  }


    </>
  )
}
