import React, { useEffect, useState } from 'react'
import { useParams ,Link } from 'react-router-dom'

export default function  ViewIt() {

    const id = useParams();
    const _id = id.id;
    //  console.log("id",_id);

  //   let url ="http://localhost:3030/singleitem"
  // console.log(url);
  //   const onsubmit = async (body) =>{

