import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function RatingTake() {
  const id = useParams();
  const _id = id.id;
  const [data, setData] = useState([]);

  const getRatingById = () => {
    // axios.get(`http://localhost:3030/rating/${_id}`)
    axios
      .get("http://localhost:3030/rating")
      .then((res) => {
        const MyData = res.data;
        console.log(MyData);
        setData(MyData);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    getRatingById();
  }, []);
  // console.log(_id);
  return (

    <>
    <div>This is hotal page</div>
  {

     data.map((item) => {
     return<>
     <div>RatingTake </div> 
     <div className="card-body">
       <h5 className="card-title">Rating : - {item.rate}</h5>
       <h5  className="card-title">Feedback : - {item.feedback}</h5>
     </div>
     </>
     }

)
  }
    </>


  );
}
