import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function RatingTake() {
  const id = useParams();
  const _id = id.id;
  console.log(_id);
  const [data, setData] = useState([]);

  const getRatingById = () => {
    axios
      .get(`http://localhost:3030/rating/${_id}`)
      .then((res) => {
        // console.log("res",res.data);
        const ratingData = res.data;
        setData(ratingData);
        // console.log(data)
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    getRatingById();
  }, []);
  console.log(data);

  //  console.log(_id);
  return (
    <>
      <div>This is hotal page</div>

      <div key={data._id} className="card">
        <h5 className="card-title">Rate : - {data.rate} </h5>
        <h5 className="card-title">Feedback : -{data.feedback}</h5>
      </div>
    </>
  );
}
