import axios from 'axios';
import React, { useState , useEffect } from 'react'
import {useParams, Link } from 'react-router-dom'

export default function Show() {
    const id = useParams();
    console.log(id);

    let url ="http://localhost:3030/singleitem"

    const onsubmit = async (body) =>{

        const rawResponse = await fetch(url, {
            method: 'POST',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({id:id})
          });
          const res=await rawResponse.json()
          console.log("result",res)

    } 

    const [data , setData] = useState([]);

    const getData = () => {
        axios.get("http://localhost:3030/getd").then((res)=>{
            console.log(res)
            const mydata = res.data
            setData(mydata)

        }).catch((error)=>{
             console.log(error)
        })
    }

    useEffect(()=>{
    getData()

    },[])

  return (
    <>
        <div>Show</div>
        {
     data.map((item) =>
     <div id="first" key={item._id} className="card">
        <img style={{height : "340px"}} src={item.hotelImg} className="card-img-top" alt="..."/>
        <div className="card-body">
        <h5 className="card-title">Hotel-Name : - {item.hName}</h5>
          <h5 className="card-title" >Address :- {item.address}</h5>
          <h5 className="card-title">Phone-Number :- {item.hotelNumber}</h5>
          <br/>

          {/* <h5 className="card-title">hotel-name{item._id}</h5> */}

          <button>
            {/* <Link to={`/view/${item._id}`}>View-More</Link> */}
            <Link to='/show'>Give-Review</Link>
          </button>
        </div>
      </div>
)
  }
    </>

  )
}
